mapper.select:
SELECT count(user_id)
FROM like_tb
WHERE
	user_id = #{userId}
	AND target_gb = #{targetGb}
	AND target_cd = #{targetCd}
	AND status = "Y";

mapper.update:
UPDATE like_tb
SET
	status = #{status},
	modified_dt = current_timestamp
WHERE
	user_id = #{userId} 
	AND target_gb = #{targetGb} 
	AND target_cd = #{targetCd}
